argument_parser = argparse.ArgumentParser (description='JSON->FASTA.')
argument_parser.add_argument('-i', '--input',   help = 'The clonotype JSON file', nargs = '?', required = True, type=argparse.FileType('r'), default = sys.stdin)
argument_parser.add_argument('-v', '--variable',   help = 'Add these V regions to the mix', nargs = '*', type=str)
argument_parser.add_argument('-j', '--joining',   help = 'Add these J regions to the mix', nargs = '*', type=str)
argument_parser.add_argument('-t', '--light_chain', help='If set, assume that the reads come from a light Ig chain (no d-region)', action = 'store_const', const = True, default = False);

cli_args = argument_parser.parse_args()

cluster_defs = json.load (cli_args.input)

if cli_args.light_chain:
    which_one = igk
else:
    which_one = igg

if cli_args.variable and cli_args.joining:
    prod = itertools.product (cli_args.variable, cli_args.joining)
    for p in prod:
        print (">%s_%s\n%s" % (p[0], p[1], which_one[p[0]] + which_one[p[1]]))

for index,cluster in enumerate(cluster_defs):
    for index2, subcluster in enumerate(cluster):
        print (">clonotype%d_%d_%d\n%s"%(index,index2,subcluster["size"],"".join(subcluster["centroid"].rstrip().split("\n")[1:])))
    
    